import React from "react";

export const ImageModal = ({ img, setOpenModal }) => {
  const closeModal = (e) => {
    setOpenModal(false);
  };
  return (
    <div className="dialog flow" onClick={(e) => closeModal(e)}>
      <button
        className="modal__close-btn uppercase"
        onClick={() => setOpenModal(false)}
      >
        Close
      </button>
      <img
        className="modal__img"
        onClick={(e) => e.stopPropagation()}
        src={img}
        alt=""
        srcset=""
      />
    </div>
  );
};
