import React from "react";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
export default function Gallery() {
  const [gallery, setGallery] = useState([]);
  const fetchData = async () => {
    const response = await fetch("/data.json");
    const data = await response.json();
    setGallery(data);
  };
  useEffect(() => {
    fetchData();
  }, []);
  return (
//     <ul class="gallery" role="list">
//   <li>
//     <figure>
//       <img alt="" src="https://picsum.photos/550/300" />
//       <figcaption>Candy canes ice cream</figcaption>
//     </figure>
//   </li>
//   <li>
//     <figure>
//       <img alt="" src="https://picsum.photos/400" />
//       <figcaption>Ice cream biscuit</figcaption>
//     </figure>
//   </li>
//   <li>
//     <figure>
//       <img alt="" src="https://picsum.photos/600/450" />
//       <figcaption>Cream biscuit marzipan</figcaption>
//     </figure>
//   </li>
// </ul>
    <main className="gallery__container">
      {gallery.map((item, index) => {
        return (
          <Link
            to={`/${item.name}`}
            key={index}
            className={`gallery__item v${index}`}
          >
            <img
              className="gallery__item__img"
              src={item.images.thumbnail}
              alt={item.name}
            />
            <div className="gallery__info">
              <h2 className="gallery__info__name">{item.name}</h2>
              <p className="gallery__info__artist">{item.artist.name}</p>
            </div>
          </Link>
        );
      })}
    </main>
  );
}
