import React from "react";

export const ImageModal = ({ img, setOpenModal }) => {
  const closeModal = (e) => {
    e.preventDefault();
    setOpenModal(false);
  };
  return (
    <div className="dialog" onClick={(e) => closeModal(e)}>
      <div className="modal">
        <button
          className="modal__close-btn"
          onClick={() => setOpenModal(false)}
        >
          CLose
        </button>
        <img src={img} alt="" srcset="" />
      </div>
    </div>
  );
};
