import { useState, useEffect } from "react";
import Navbar from "../components/Navbar";
import { useParams } from "react-router-dom";
function SlideShow() {
  const [gallery, setGallery] = useState([]);
  const { name } = useParams();
  const fetchData = async () => {
    const response = await fetch("/data.json");
    const data = await response.json();

    setGallery(data.filter((item) => item.name === name));
  };
  useEffect(() => {
    fetchData();
  }, []);

  return (
    <>
      <div className="single-gallery">
        <Navbar></Navbar>
        <main className="container">
          {gallery.map((item, index) => {
            const { name, artist, images, description } = item;
            return (
              <>
                <div key={index} className="container__info grid">
                  <img className="container__info__hero-img" src={images.gallery} alt={name} />
                  <div className="container__info__brief">
                    <h2 className="container__info__brief--name">{name}</h2>
                    <p className="container__info__brief--artist">
                      {artist.name}
                    </p>
                    <img src={artist.image} alt="" />
                  </div>
                </div>
                <div className="container__content">
                  <p>{description}</p>
                </div>
              </>
            );
          })}
        </main>
        <div className="slideshow_handle">
          <div className="slideshow__handle__info"></div>
        </div>
      </div>
    </>
  );
}

export default SlideShow;
